import React from "react";
import styled from "styled-components";
import { usePetContext } from "../context/PetContext";

interface Pet {
  id: string;
  imageUrl: string;
  title: string;
  description: string;
  createdAt: string;
}

const ImageCard: React.FC<{ pet: Pet }> = ({ pet }) => {
  const { selectedPets, toggleSelectPet } = usePetContext();

  return (
    <Card onClick={() => toggleSelectPet(pet.id)}>
      <StyledImage src={require(`../img/${pet.imageUrl}`)} alt={pet.title} />
      <h3>{pet.title}</h3>
      <p>{pet.description}</p>
      <p>{new Date(pet.createdAt).toLocaleDateString()}</p>
      <input type="checkbox" checked={selectedPets.includes(pet.id)} readOnly />
    </Card>
  );
};

export default ImageCard;

// Styled Components
const Card = styled.div`
  border: 1px solid #ddd;
  padding: 16px;
  margin: 8px;
  display: inline-block;
  text-align: center;
`;

const StyledImage = styled.img`
  width: 100%; /* Makes image responsive */
  max-width: 200px; /* Sets a max width */
  height: auto; /* Keeps aspect ratio */
  border-radius: 8px; /* Adds slight rounded corners */
`;
