import React, { createContext, useContext, useState } from "react";

// Define the structure of the context's values and functions
interface PetContextProps {
  selectedPets: string[]; // Array of selected pet IDs
  toggleSelectPet: (id: string) => void; // Function to toggle selection of a pet by ID
  selectAllPets: (ids: string[]) => void; // Function to select all pets
  clearSelection: () => void; // Function to clear all selections
}

// Create the context with an initial undefined value
const PetContext = createContext<PetContextProps | undefined>(undefined);

// PetProvider component to manage global state for selected pets
export const PetProvider: React.FC<{ children: React.ReactNode }> = ({
  children,
}) => {
  // State to track selected pet IDs
  const [selectedPets, setSelectedPets] = useState<string[]>([]);

  // Function to toggle a pet's selection state
  const toggleSelectPet = (id: string) => {
    setSelectedPets((prev) =>
      prev.includes(id) ? prev.filter((petId) => petId !== id) : [...prev, id]
    );
  };

  // Function to select all pets by setting all IDs in the state
  const selectAllPets = (ids: string[]) => {
    setSelectedPets(ids);
  };

  // Function to clear all selections by resetting the state
  const clearSelection = () => {
    setSelectedPets([]);
  };

  // Provide the context values and functions to child components
  return (
    <PetContext.Provider
      value={{ selectedPets, toggleSelectPet, selectAllPets, clearSelection }}
    >
      {children}
    </PetContext.Provider>
  );
};

// Custom hook to access the PetContext in child components
export const usePetContext = () => {
  const context = useContext(PetContext);
  if (!context) {
    throw new Error("usePetContext must be used within a PetProvider");
  }
  return context;
};
